mapper.update:
UPDATE infrMember SET
ifmmName = #{ifmmName}
where 1 = 1
and ifmmSeq = #{ifmmSeq}

mapper.select:
select a.ifmmSeq ,a.ifmmAdminNy ,a.ifmmDormancyNy ,a.ifmmName ,a.ifmmNickname ,a.ifmmPhoto ,a.ifmmId ,a.ifmmPassword ,a.ifmmGrade ,a.ifmmGenderCd ,a.ifmmDob ,a.ifmmSaveCd ,a.ifmmFavoriteColor ,a.ifmmEmailConsentNy ,a.ifmmSmsConsentNy ,a.ifmmPushConsentNy ,a.ifmmDelNy ,a.ifmmMarriageCd ,a.ifmmMarriageDate ,a.ifmmChildrenNum ,a.regDateTime ,a.modDateTime ,a.modSeq ,b.ifmaTypeCd ,b.ifmaTitle ,b.ifmaAddress1 ,b.ifmaAddress2 ,b.ifmaZipCode ,c.ifaoTypeCd ,c.ifaoSnsTypeCd ,c.ifaoTitle ,c.ifaoUrl ,d.ifmeTypeCd ,d.ifmeEmailFull ,e.ifjqQuestionCd ,e.ifjqAnswer ,f.ifnaSeq ,g.ifnaName ,h.ifmpTypeCd ,h.ifmpDeviceCd ,h.ifmpTelecomCd ,h.ifmpNumber
from infrMember a left join infrMemberAddress b on b.ifmmSeq = a.ifmmSeq and ifmaDefaultNy = 1 left join infrMemberAddressOnline c on c.ifmmSeq= a.ifmmSeq and ifaoDefaultNy = 1 left join infrMemberEmail d on d.ifmmSeq = a.ifmmSeq and ifmeDefaultNy = 1 left join infrMemberJoinqna e on e.ifmmSeq = a.ifmmSeq left join infrMemberNationality f on f.ifmmSeq = a.ifmmSeq and ifmnDefaultNy = 1 left join infrNationality g on g.ifnaSeq = f.ifnaSeq left join infrMemberPhone h on h.ifmmSeq = a.ifmmSeq and ifmpDefaultNy = 1
where 1=1 and a.ifmmSeq = #{ifmmSeq}

